#
# 远亲不如近邻
# @param n int整型 居民个数
# @param m int整型 方案个数
# @param a int整型一维数组 居民的位置
# @param x int整型一维数组 方案对应的位置
# @return int整型一维数组
#
class Solution:
    def min_arr(self, a, x_):
        low = 0
        high = len(a) - 1
        
        # 特殊情况可以提前排除掉
        # 比如位于所有居民的左侧或者所有居民的右侧
        
        min_a = a[0]
        max_a = a[-1]
        if x_ < min_a:
            return min_a - x_
        if x_ > max_a:
            return x_ - max_a

        # 除了特殊情况外，则应该就都位于两个数字之间了
        while low < high:
            mid = (high + low)//2
            
            # 如果刚好找到了，则对应的距离应该是0
            if a[mid] == x_:
                return 0
            
            # 否则，继续缩小可能区间
            if a[mid] < x_:
                # 需要确认下一步是否会超过x_，如果是，则已经找到了
                if a[mid+1] > x_:
                    # print("a[mid] < x_", x_ - a[mid], )
                    return min(x_ - a[mid], a[mid+1] - x_)
                else:
                    low = mid + 1
            else:
                if a[mid-1] < x_:
                    return min(a[mid] - x_, x_ - a[mid-1])
                high = mid - 1
                

    def solve(self , n , m , a , x ):
        # write code here
        dists = []
        a.sort()
        for x_ in x:
            # 缩小范围，找到最近居民所在的区间
            dist = self.min_arr(a, x_)
            dists.append(dist)
        return dists

